package Taskprogram;

public class absumcdgreater {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=70, b=20, c=30, d=50;
		int x, y;
		x = a+b;
		y  = c+d;
		
		System.out.println("The Sum of x value is: " + x);
		System.out.println("The Sum of y value is: " + y);
		System.out.println();
		
		if (x > y)
		{
			System.out.println("The sum of a and b value is greater than c and d");
			
		}
		
		else
		{
			System.out.println("The sum of a and b value is less than c and d");
		}
			
	}

}
